    public static ArrayList<Wisata> getListPlace(){
        ArrayList<Wisata> list = new ArrayList<>();
        for (String[] inidata : data){
            Wisata iniwisata = new Wisata();
            iniwisata.setWisata(inidata[0]);
            iniwisata.setAlamat(inidata[1]);
            iniwisata.setDeskripsi(inidata[2]);
            iniwisata.setGambar(inidata[3]);
            iniwisata.setRating(inidata[4]);
            iniwisata.setCategory(inidata[5]);
            iniwisata.setImg1(inidata[6]);
            iniwisata.setNameImg1(inidata[7]);
            iniwisata.setImg2(inidata[8]);
            iniwisata.setNameImg2(inidata[9]);
            iniwisata.setImg3(inidata[10]);
            iniwisata.setNameImg3(inidata[11]);
            iniwisata.setTag1(inidata[12]);
            iniwisata.setTag2(inidata[13]);
            iniwisata.setTag3(inidata[14]);
            iniwisata.setContent(inidata[15]);
            list.add(iniwisata);
        }
        return list;
    }
